      adaptiveHeight,
      adaptiveHeightAnimation,
      afterSlide,
      animation,
      autoplay,
      autoplayInterval,
      autoplayReverse,
      beforeSlide,
      cellAlign: propsCellAlign,
      cellSpacing,
      children,
      className,
      disableAnimation,
      dragging: desktopDraggingEnabled,
      dragThreshold: propsDragThreshold,
      enableKeyboardControls,
      frameAriaLabel,
      keyCodeConfig,
      onDrag,
      onDragEnd,
      onDragStart,
      onUserNavigation,
      pauseOnHover,
      renderAnnounceSlideMessage,
      scrollMode: propsScrollMode,
      slideIndex,
      slidesToScroll: propsSlidesToScroll,
      slidesToShow: propsSlidesToShow,
      slideWidth,
      speed,
      style,
      swiping: mobileDraggingEnabled,
      wrapAround,
      zoomScale,
    } = props;

    const filteredSlides = React.Children.toArray(children).filter(Boolean);
    const slideCount = filteredSlides.length;

    const cellAlign: CellAlign =
      slideWidth || propsSlidesToScroll === 'auto' ? 'left' : propsCellAlign;

    const scrollMode: ScrollMode =
      propsSlidesToScroll === 'auto' ? ScrollMode.remainder : propsScrollMode;

    const [slideIOEntries, setSlideIOEntries] = useState(
      new Map<string, boolean>()
