export const useTween = (
  durationMs: number,
  easingFunction: EasingFunction,
  // navigationNum is an combination of numbers that are stable when the
  // animation should not be running or should continue running, but change when
  // the animation should start running. In practice, this is a combination of
  // the animation distance and slide index.
  navigationNum: number,
  shouldInterrupt: boolean
) => {
  const [normalizedTimeRaw, setNormalizedTime] = useState(1);
  const startTime = useRef(Date.now());
  const rAF = useRef<number | undefined>();
  const isFirstRender = useRef(true);
  const lastNavigationNum = useRef<number | null>(null);

  // Detect on the first render following navigation if the animation should
  // be running. If we wait for the useEffect, the first render will flash with
  // the slide in its destination position, before the animation triggers,
  // sending it back to the position of the first frame of the animation. This
  // approach is done in place of a useLayoutEffect, which has issues with SSR.
  const normalizedTime =
    lastNavigationNum.current === null ||
    lastNavigationNum.current === navigationNum ||
    shouldInterrupt
      ? normalizedTimeRaw
      : 0; // 0 here indicates the animation has begun

  useEffect(() => {
    lastNavigationNum.current = navigationNum;

    // Skip the first render as we don't want to trigger the animation right off
    // the bat
    if (isFirstRender.current) {
      isFirstRender.current = false;
      return;
    }

    if (shouldInterrupt) {
      return;
    }

    startTime.current = Date.now();
    setNormalizedTime(0);

    const tick = () => {
      rAF.current = requestAnimationFrame(() => {
        const currentTime = Date.now();
        const normalizedTime = Math.min(
          1,
          (currentTime - startTime.current) / durationMs
        );
        setNormalizedTime(normalizedTime);

        if (normalizedTime < 1) {
          tick();
        } else {
          // Clean up so we can use this value to determine if the most recent
          // animation completed
          rAF.current = undefined;
        }
      });
    };
    tick();

    return () => {
      // If the most recent animation did not complete, cut it short and reset
      // the animation
      if (rAF.current !== undefined) {
        cancelAnimationFrame(rAF.current);
        setNormalizedTime(1);
      }
    };
  }, [navigationNum, durationMs, shouldInterrupt]);

  return {
    isAnimating: normalizedTime !== 1,
    value: easingFunction(normalizedTime),
  };
};
import React, { Fragment } from 'react';
import { getControlContainerStyles } from './control-styles';
import {
  getDotIndexes,
  nextButtonDisabled,
  prevButtonDisabled,
} from './default-controls';
import {
  InternalCarouselProps,
  Positions,
  RenderControlFunctionNames,
} from './types';

type ControlMap = { funcName: RenderControlFunctionNames; key: Positions }[];
const controlsMap: ControlMap = [
  { funcName: 'renderTopLeftControls', key: Positions.TopLeft },
  { funcName: 'renderTopCenterControls', key: Positions.TopCenter },
  { funcName: 'renderTopRightControls', key: Positions.TopRight },
  { funcName: 'renderCenterLeftControls', key: Positions.CenterLeft },
  { funcName: 'renderCenterCenterControls', key: Positions.CenterCenter },
  { funcName: 'renderCenterRightControls', key: Positions.CenterRight },
  { funcName: 'renderBottomLeftControls', key: Positions.BottomLeft },
  { funcName: 'renderBottomCenterControls', key: Positions.BottomCenter },
  { funcName: 'renderBottomRightControls', key: Positions.BottomRight },
];

const renderControls = (
  props: InternalCarouselProps,
  slideCount: number,
  currentSlide: number,
  goToSlide: (targetSlideIndex: number) => void,
  nextSlide: () => void,
  prevSlide: () => void,
  slidesToScroll: number
): React.ReactElement[] | null => {
  if (props.withoutControls) {
    return null;
  }

  const disableCheckProps = {
    ...props,
    currentSlide,
    slideCount,
  };
  const nextDisabled = nextButtonDisabled(disableCheckProps);
  const previousDisabled = prevButtonDisabled(disableCheckProps);
  const pagingDotsIndices = getDotIndexes(
    slideCount,
    slidesToScroll,
    props.scrollMode,
    props.slidesToShow,
    props.wrapAround,
    props.cellAlign
  );

  return controlsMap.map((control) => {
    if (
      !props[control.funcName] ||
      typeof props[control.funcName] !== 'function'
    ) {
      return <Fragment key={control.funcName} />;
    }
    return (
      <div
        key={control.funcName}
        style={{
          ...getControlContainerStyles(control.key),
          pointerEvents: 'none',
        }}
      >
        <div
          className={[
            `slider-control-${control.key.toLowerCase()}`,
            props.defaultControlsConfig.containerClassName || '',
          ]
            .join(' ')
            .trim()}
          // The container has `pointerEvents: 'none'` so we need to override
          // that to make sure the controls are clickable.
          style={{ pointerEvents: 'auto' }}
        >
          {props[control.funcName]?.({
            cellAlign: props.cellAlign,
            cellSpacing: props.cellSpacing,
            currentSlide,
            defaultControlsConfig: props.defaultControlsConfig || {},
            pagingDotsIndices,
            goToSlide,
            nextDisabled,
            nextSlide,
            onUserNavigation: props.onUserNavigation,
            previousDisabled,
            previousSlide: prevSlide,
            scrollMode: props.scrollMode,
            slideCount,
            slidesToScroll,
            slidesToShow: props.slidesToShow || 1,
            vertical: props.vertical,
            wrapAround: props.wrapAround,
          })}
        </div>
      </div>
    );
  });
};

export default renderControls;
import { CSSProperties } from 'react';
import { Positions } from './types';

const commonStyles: CSSProperties = {
  position: 'absolute',
  display: 'flex',
  zIndex: 1,
  top: 0,
  left: 0,
  bottom: 0,
  right: 0,
};

/**
 * Gets flexbox alignment and justify-content styles for a given position.
 */
const getControlContainerFlexStyles = (pos: Positions): CSSProperties => {
  let alignItems: CSSProperties['alignItems'];

  switch (pos) {
    case Positions.TopLeft:
    case Positions.TopCenter:
    case Positions.TopRight:
      alignItems = 'flex-start';
      break;
    case Positions.CenterLeft:
    case Positions.CenterCenter:
    case Positions.CenterRight:
      alignItems = 'center';
      break;
    case Positions.BottomLeft:
    case Positions.BottomCenter:
    case Positions.BottomRight:
      alignItems = 'flex-end';
      break;
  }

  let justifyContent: CSSProperties['justifyContent'];
  switch (pos) {
    case Positions.TopLeft:
    case Positions.CenterLeft:
    case Positions.BottomLeft:
      justifyContent = 'flex-start';
      break;
    case Positions.TopCenter:
    case Positions.CenterCenter:
    case Positions.BottomCenter:
      justifyContent = 'center';
      break;
    case Positions.TopRight:
    case Positions.CenterRight:
    case Positions.BottomRight:
      justifyContent = 'flex-end';
      break;
  }

  return { alignItems, justifyContent };
};

/**
 * Gets the styles for a back/forward control container to align the control
 * properly within the parent.
 */
export const getControlContainerStyles = (pos: Positions): CSSProperties => {
  return { ...getControlContainerFlexStyles(pos), ...commonStyles };
};
